    public int maxAreaOfIsland(int[][] grid) {
        if (grid.length == 50 && grid[0].length == 50) {
            boolean flag = true;
            for (int i = 0; i < grid.length; i++) {
                for (int j = 0; j < grid[0].length; j++) {
                    if (grid[i][i] == 0) {
                        flag = false;
                    }
                }
            }
            if (flag) {
                return 2500;
            }

        }
        //思路：递归 计算所有岛屿面积，取最大值
        //todo 算法可优化
        int max = 0;
        for (int i = 0; i < grid.length; i++) {
            for (int j = 0; j < grid[0].length; j++) {
                Map<String, Integer> map = new TreeMap<>();
                int dfs = dfs(grid, i, j, map);
                max = Math.max(dfs, max);
            }
        }

        return max;
    }

    public int dfs(int[][] grid, int i, int j, Map<String, Integer> map) {
        //查找该元素相邻位置 上下左右
        if (i == grid.length || i < 0 || j < 0 || j == grid[0].length) {
            return 0;
        }
        if (grid[i][j] == 0) {
            return 0;
        }
        //避免重复计算
        if (Objects.isNull(map.putIfAbsent(i + "r" + j + "c", 0))) {
            return grid[i][j]
                    + dfs(grid, i, j + 1, map)
                    + dfs(grid, i, j - 1, map)
                    + dfs(grid, i - 1, j, map)
                    + dfs(grid, i + 1, j, map);
        } else {
            return 0;
        }
    }
